mapper.update:
update payments 


    pay_status=#{payStatus},


    pay_number=#{payNumber},


    end_time=#{endTime},


    pay_app=#{payApp}


    refunds_number=#{refundsNumber}


    refund_total=#{refundTotal}


    pay_refund=#{payRefund}


    refund_desc=#{refundDesc}


    refuns_time=#{refunsTime}


where payments_id=#{paymentsId}
